examples.source:
import G6 from '@antv/g6';

const data = {
  nodes: [],
  edges: [],
};

for (let i = 1; i < 60; i++) {
  const id = `node-${i}`;
  const month = i < 30 ? '01' : '02';
  const day = i % 30 < 10 ? `0${i % 30}` : `${i % 30}`;
  data.nodes.push({
    id,
    date: parseInt(`2020${month}${day}`),
    value: Math.round(Math.random() * 300),
    label: parseInt(`2020${i}`),
  });

  data.edges.push({
    source: `node-${Math.round(Math.random() * 60)}`,
    target: `node-${Math.round(Math.random() * 60)}`,
  });
}

const timeBarData = [];

for (let i = 1; i < 60; i++) {
  const month = i < 30 ? '01' : '02';
  const day = i % 30 < 10 ? `0${i % 30}` : `${i % 30}`;
  timeBarData.push({
    date: parseInt(`2020${month}${day}`),
    value: Math.round(Math.random() * 300),
  });
}

const container = document.getElementById('container');
const width = container.scrollWidth;
const height = (container.scrollHeight || 500) - 100;

const nodeSize = 20;

let count = 0;
const timebar = new G6.TimeBar({
  x: 0,
  y: 0,
  width,
  height: 150,
  padding: 10,
  type: 'tick',
  tick: {
    data: timeBarData,
    width: width,
    height: 42,
    padding: 2,
    tickLabelFormatter: (d) => {
      count++;
      const dateStr = `${d.date}`;
      if ((count - 1) % 10 === 0) {
        return `${dateStr.substr(0, 4)}-${dateStr.substr(4, 2)}-${dateStr.substr(6, 2)}`;
      }
      return false;
    },
    tooltipFomatter: (d) => {
      const dateStr = `${d}`;
      return `${dateStr.substr(0, 4)}-${dateStr.substr(4, 2)}-${dateStr.substr(6, 2)}`;
    },
  },
});

