      maxIteration: 200,
      focusNode: '2',
      unitRadius: unitRadius,
      linkDistance: 180,
      preventOverlap: true
    });
    subRadialLayout.init({
      nodes: newNodeModels,
      edges: newEdgeModels
    });
    subRadialLayout.execute();
    graph.positionsAnimate();
    graph.data({
      nodes: allNodeModels,
      edges: allEdgeModels
    });
  });
});

