const width = container.scrollWidth;
const height = (container.scrollHeight || 500) - 30;

const graph = new G6.Graph({
  container: 'container',
  width,
  height,
  modes: {
    default: ['drag-canvas', 'drag-node'],
  },
  layout: {
    type: 'circular',
  },
  animate: true,
  defaultNode: {
    size: 20,
  },
  defaultEdge: {
    style: {
