  };

layoutConfigTranslation();

setInterval(function () {
  layoutConfigTranslation();
}, 11500);

function layoutConfigTranslation() {
  setTimeout(function () {
    descriptionDiv.innerHTML = 'Circular layout, radius = 200, divisions = 5, ordering: degree';
    graph.updateLayout({
      radius: 200,
      startAngle: Math.PI / 4,
      endAngle: Math.PI,
      divisions: 5,
      ordering: 'degree',
    });
  }, 1000);

  setTimeout(function () {
    descriptionDiv.innerHTML = 'Circular layout, radius = 200, divisions = 3, ordering: degree';
    graph.updateLayout({
      startAngle: Math.PI / 4,
      endAngle: Math.PI,
      divisions: 3,
    });
  }, 2500);

  setTimeout(function () {
    descriptionDiv.innerHTML = 'Circular layout, radius = 200, divisions = 8, ordering: degree';
    graph.updateLayout({
      radius: 200,
      startAngle: 0,
      endAngle: Math.PI / 2,
      divisions: 8,
    });
  }, 4000);

  setTimeout(function () {
    descriptionDiv.innerHTML =
      'Circular layout, radius = 10～300(spiral), endAngle: PI, divisions = 1, ordering: degree';
    graph.updateLayout({
      radius: null,
      startRadius: 10,
      endRadius: 300,
      divisions: 1,
      startAngle: 0,
      endAngle: Math.PI,
    });
  }, 5500);

  setTimeout(function () {
    descriptionDiv.innerHTML =
      'Circular layout, radius = 10～300(spiral),endAngle: 2 * PI, divisions= 1, ordering: degree';
    graph.updateLayout({
      endAngle: 2 * Math.PI,
    });
  }, 7000);

  setTimeout(function () {
    descriptionDiv.innerHTML = 'Circular layout, radius = 200, ordering: degree';
    graph.updateLayout({
      radius: 200,
    });
  }, 8500);

  setTimeout(function () {
    descriptionDiv.innerHTML = 'Circular layout, radius = 200, ordering: topology';
    graph.updateLayout({
      radius: 200,
      ordering: 'topology',
    });
  }, 10000);
}

