          fill: config.basicColor,
          radius: 1.5
        },
        name: 'left-border-shape'
      });
      return container;
    },

    /* 生成树上的 marker */
    createNodeMarker: function createNodeMarker(group, collapsed, x, y) {
      group.addShape('circle', {
        attrs: {
          x: x,
          y: y,
          r: 13,
          fill: 'rgba(47, 84, 235, 0.05)',
          opacity: 0,
          zIndex: -2
        },
        name: 'collapse-icon-bg'
      });
      group.addShape('marker', {
        attrs: {
          x: x,
          y: y,
          r: 7,
          symbol: collapsed ? EXPAND_ICON : COLLAPSE_ICON,
          stroke: 'rgba(0,0,0,0.25)',
          fill: 'rgba(0,0,0,0)',
          lineWidth: 1,
          cursor: 'pointer'
        },
        name: 'collapse-icon'
      });
    },
    afterDraw: function afterDraw(cfg, group) {
      /* 操作 marker 的背景色显示隐藏 */
      var icon = group.find(function (element) {
        return element.get('name') === 'collapse-icon';
      });

      if (icon) {
        var bg = group.find(function (element) {
          return element.get('name') === 'collapse-icon-bg';
        });
        icon.on('mouseenter', function () {
          bg.attr('opacity', 1);
          graph.get('canvas').draw();
        });
        icon.on('mouseleave', function () {
          bg.attr('opacity', 0);
          graph.get('canvas').draw();
        });
      }
      /* ip 显示 */


      var ipBox = group.find(function (element) {
        return element.get('name') === 'ip-box';
      });

      if (ipBox) {
        /* ip 复制的几个元素 */
        var ipLine = group.find(function (element) {
          return element.get('name') === 'ip-cp-line';
        });
        var ipBG = group.find(function (element) {
          return element.get('name') === 'ip-cp-bg';
        });
        var ipIcon = group.find(function (element) {
          return element.get('name') === 'ip-cp-icon';
        });
        var ipCPBox = group.find(function (element) {
          return element.get('name') === 'ip-cp-box';
        });

        var onMouseEnter = function onMouseEnter() {
          ipLine.attr('opacity', 1);
          ipBG.attr('opacity', 1);
          ipIcon.attr('opacity', 1);
          graph.get('canvas').draw();
        };

        var onMouseLeave = function onMouseLeave() {
          ipLine.attr('opacity', 0);
          ipBG.attr('opacity', 0);
          ipIcon.attr('opacity', 0);
          graph.get('canvas').draw();
        };

        ipBox.on('mouseenter', function () {
          onMouseEnter();
        });
        ipBox.on('mouseleave', function () {
          onMouseLeave();
        });
        ipCPBox.on('mouseenter', function () {
          onMouseEnter();
        });
        ipCPBox.on('mouseleave', function () {
          onMouseLeave();
        });
        ipCPBox.on('click', function () {});
      }
    },
    setState: function setState(name, value, item) {
      var hasOpacityClass = ['ip-cp-line', 'ip-cp-bg', 'ip-cp-icon', 'ip-cp-box', 'ip-box', 'collapse-icon-bg'];
      var group = item.getContainer();
      var childrens = group.get('children');
      graph.setAutoPaint(false);

      if (name === 'emptiness') {
        if (value) {
          childrens.forEach(function (shape) {
            if (hasOpacityClass.indexOf(shape.get('name')) > -1) {
              return;
            }

            shape.attr('opacity', 0.4);
          });
        } else {
          childrens.forEach(function (shape) {
            if (hasOpacityClass.indexOf(shape.get('name')) > -1) {
              return;
            }

            shape.attr('opacity', 1);
          });
        }
      }

      graph.setAutoPaint(true);
    }
  };

  _g.default.registerNode('card-node', {
    draw: function draw(cfg, group) {
      var config = getNodeConfig(cfg);
      var isRoot = cfg.dataType === 'root';
      var nodeError = cfg.nodeError;
      /* the biggest rect */

      var container = nodeBasicMethod.createNodeBox(group, config, 243, 64, isRoot);

      if (cfg.dataType !== 'root') {
        /* the type text */
        group.addShape('text', {
          attrs: {
            text: cfg.dataType,
            x: 3,
            y: -10,
            fontSize: 12,
            textAlign: 'left',
            textBaseline: 'middle',
            fill: 'rgba(0,0,0,0.65)'
          },
          name: 'type-text-shape'
        });
      }

      if (cfg.ip) {
        /* ip start */

        /* ipBox */
        var ipRect = group.addShape('rect', {
          attrs: {
            fill: nodeError ? null : '#FFF',
            stroke: nodeError ? 'rgba(255,255,255,0.65)' : null,
            radius: 2,
            cursor: 'pointer'
          },
          name: 'ip-container-shape'
        });
        /* ip */

        var ipText = group.addShape('text', {
          attrs: {
            text: cfg.ip,
            x: 0,
            y: 19,
            fontSize: 12,
            textAlign: 'left',
            textBaseline: 'middle',
            fill: nodeError ? 'rgba(255,255,255,0.85)' : 'rgba(0,0,0,0.65)',
            cursor: 'pointer'
          },
          name: 'ip-text-shape'
        });
        var ipBBox = ipText.getBBox();
        /* the distance from the IP to the right is 12px */

        ipText.attr({
          x: 224 - 12 - ipBBox.width
        });
        /* ipBox */

        ipRect.attr({
          x: 224 - 12 - ipBBox.width - 4,
          y: ipBBox.minY - 5,
          width: ipBBox.width + 8,
          height: ipBBox.height + 10
        });
        /* a transparent shape on the IP for click listener */

        group.addShape('rect', {
          attrs: {
            stroke: '',
            cursor: 'pointer',
            x: 224 - 12 - ipBBox.width - 4,
            y: ipBBox.minY - 5,
            width: ipBBox.width + 8,
            height: ipBBox.height + 10,
            fill: '#fff',
            opacity: 0
          },
          name: 'ip-box'
        });
        /* copyIpLine */

        group.addShape('rect', {
          attrs: {
            x: 194,
            y: 7,
            width: 1,
            height: 24,
            fill: '#E3E6E8',
            opacity: 0
          },
          name: 'ip-cp-line'
        });
        /* copyIpBG */

        group.addShape('rect', {
          attrs: {
            x: 195,
            y: 8,
            width: 22,
            height: 22,
            fill: '#FFF',
            cursor: 'pointer',
            opacity: 0
          },
          name: 'ip-cp-bg'
        });
        /* copyIpIcon */

        group.addShape('image', {
          attrs: {
            x: 200,
            y: 13,
            height: 12,
            width: 10,
            img: 'https://os.alipayobjects.com/rmsportal/DFhnQEhHyPjSGYW.png',
            cursor: 'pointer',
            opacity: 0
          },
          name: 'ip-cp-icon'
        });
        /* a transparent rect on the icon area for click listener */

        group.addShape('rect', {
          attrs: {
            x: 195,
            y: 8,
            width: 22,
            height: 22,
            fill: '#FFF',
            cursor: 'pointer',
            opacity: 0
          },
          name: 'ip-cp-box',
          tooltip: 'Copy the IP'
        });
        /* ip end */
      }
      /* name */


      group.addShape('text', {
        attrs: {
          text: cfg.name,
          x: 19,
          y: 19,
          fontSize: 14,
          fontWeight: 700,
          textAlign: 'left',
          textBaseline: 'middle',
          fill: config.fontColor,
          cursor: 'pointer'
        },
        name: 'name-text-shape'
      });
      /* the description text */

      group.addShape('text', {
        attrs: {
          text: cfg.keyInfo,
          x: 19,
          y: 45,
          fontSize: 14,
          textAlign: 'left',
          textBaseline: 'middle',
          fill: config.fontColor,
          cursor: 'pointer'
        },
        name: 'bottom-text-shape'
      });

      if (nodeError) {
        group.addShape('text', {
          attrs: {
            x: 191,
            y: 62,
            text: '⚠️',
            fill: '#000',
            fontSize: 18
          },
          name: 'error-text-shape'
        });
      }

      var hasChildren = cfg.children && cfg.children.length > 0;

      if (hasChildren) {
        nodeBasicMethod.createNodeMarker(group, cfg.collapsed, 236, 32);
      }

      return container;
    },
    afterDraw: nodeBasicMethod.afterDraw,
    setState: nodeBasicMethod.setState
  });

  var container = document.getElementById('container');
  var width = container.scrollWidth;
  var height = container.scrollHeight || 500;
  graph = new _g.default.Graph({
    container: 'container',
    width: width,
    height: height,
    // translate the graph to align the canvas's center, support by v3.5.1
    fitCenter: true,
    modes: {
      default: ['drag-node']
    },
    defaultNode: {
      type: 'card-node'
    }
  });
  var data = {
    nodes: [{
      name: 'cardNodeApp',
      ip: '127.0.0.1',
      nodeError: true,
      dataType: 'root',
      keyInfo: 'this is a card node info',
      x: 100,
      y: 50
    }, {
      name: 'cardNodeApp',
      ip: '127.0.0.1',
      nodeError: false,
      dataType: 'subRoot',
      keyInfo: 'this is sub root',
      x: 100,
      y: 150
    }, {
      name: 'cardNodeApp',
      ip: '127.0.0.1',
      nodeError: false,
      dataType: 'subRoot',
      keyInfo: 'this is sub root',
      x: 100,
      y: 250,
      children: [{
        name: 'sub'
      }]
    }],
    edges: []
  };
  graph.data(data);
  graph.render();
  if (typeof window !== 'undefined') window.onresize = function () {
    if (!graph || graph.get('destroyed')) return;
    if (!container || !container.scrollWidth || !container.scrollHeight) return;
    graph.changeSize(container.scrollWidth, container.scrollHeight);
  };
});

