      raw: {},
      children: [{
        id: 'SubTreeNode1.1'
      }, {
        id: 'SubTreeNode1.2',
        children: [{
          id: 'SubTreeNode1.2.1'
        }, {
          id: 'SubTreeNode1.2.2'
        }, {
          id: 'SubTreeNode1.2.3'
        }]
      }]
    }, {
      id: 'SubTreeNode2',
      children: [{
        id: 'SubTreeNode2.1'
      }]
    }, {
      id: 'SubTreeNode3',
      children: [{
        id: 'SubTreeNode3.1'
      }, {
        id: 'SubTreeNode3.2'
      }, {
        id: 'SubTreeNode3.3'
      }]
    }, {
      id: 'SubTreeNode4'
    }, {
      id: 'SubTreeNode5'
    }, {
      id: 'SubTreeNode6'
    }]
  };
  graph.data(data);
  graph.render();
  var count = 0;
  graph.on('node:click', function (evt) {
    var item = evt.item;
    var nodeId = item.get('id');
    var model = item.getModel();
    var children = model.children;

    if (!children || children.length === 0) {
      var childData = {
        id: 'child-data-' + count,
        type: 'rect',
        children: [{
          id: 'x-' + count
        }, {
          id: 'y-' + count
        }]
      };
      graph.addChild(childData, nodeId);
      count++;
    }
