  var height = document.getElementById('container').scrollHeight || 500;
  var graph = new _g.default.Graph({
    container: 'container',
    width: width,
    height: height,
    // translate the graph to align the canvas's center, support by v3.5.1
    fitCenter: true,
    plugins: [legend],
    modes: {
      default: ['drag-canvas', 'drag-node']
    },
    layout: {
      type: 'radial',
      focusNode: 'li',
      linkDistance: 200,
      unitRadius: 200
    },
    defaultEdge: {
      style: {
        endArrow: true
      },
      labelCfg: {
        autoRotate: true,
        style: {
          stroke: "#fff",
          lineWidth: 5
        }
      }
    },
    defaultNode: {
      type: 'donut',
      style: {
        lineWidth: 0
      },
      labelCfg: {
        position: 'bottom'
      }
    }
  });
  graph.data(data);
  graph.render();
  graph.on('node:mouseenter', function (evt) {
    var item = evt.item;
    graph.setItemState(item, 'active', true);
  });
  graph.on('node:mouseleave', function (evt) {
    var item = evt.item;
    graph.setItemState(item, 'active', false);
  });
  graph.on('node:click', function (evt) {
    var item = evt.item;
    graph.setItemState(item, 'selected', true);
  });
  graph.on('canvas:click', function (evt) {
    graph.getNodes().forEach(function (node) {
      graph.clearItemStates(node);
    });
  });
});

