        graph.setItemState(item, 'dark', false);
      });
      edgeItems.forEach(function (item) {
        graph.setItemState(item, 'dark', false);
      });
    }
  });
  fetch('https://gw.alipayobjects.com/os/bmw-prod/fc6e64fc-be94-40fb-b9e2-2d13dd414f38.json').then(function (res) {
    return res.json();
  }).then(function (data) {
    loadData(data);
  }); // click root to expand

  graph.on('node:click', function (e) {
    curShowNodes = [];
    curShowEdges = [];
    var item = e.item;
    var model = item.getModel();

    if (!model.isLeaf && model.level !== 0) {
      return;
    } // if clicked a root, hide unrelated items and show the related items


    if (model.level === 0) {
      var layoutController = graph.get('layoutController');
      var forceLayout = layoutController.layoutMethods[0];
      forceLayout.forceSimulation.stop(); // light the level 0 nodes

      showNodes.forEach(function (snode) {
        var item = graph.findById(snode.id);
        graph.setItemState(item, 'dark', false);

        if (snode.x < 0.5 * width) {
          snode.x = 300;
        } else {
          snode.x = width - 300;
        }
      });
      model.x = width / 2;
      model.y = height / 2; // animatively hide the items which are going to disappear

      if (curShowEdges.length) {
        curShowEdges.forEach(function (csedge) {
          var item = graph.findById(csedge.id);
          item && graph.setItemState(item, 'disappearing', true);
        });
      }

      curShowNodes.forEach(function (csnode) {
        var item = graph.findById(csnode.id);
        item && graph.setItemState(item, 'disappearing', true);
      });
      graph.positionsAnimate(); // reset curShowNodes nad curShowEdges

      curShowNodes = [];
      curShowEdges = []; // click on the same node which is the current focus node, hide the small nodes, change the layout parameters to roots view

      if (currentFocus && currentFocus.id === model.id) {
        currentFocus = undefined;
        layoutController.layoutCfg.nodeStrength = 2500;
        layoutController.layoutCfg.collideStrength = 0.8;
        layoutController.layoutCfg.alphaDecay = 0.01;
      } else {
        // click other focus node, hide the current small nodes and show the related nodes
        currentFocus = model; // change data after the original items disappearing

        var _layoutController = graph.get('layoutController');

        _layoutController.layoutCfg.nodeStrength = function () {
          return -80;
        };

        _layoutController.layoutCfg.collideStrength = 0.2;

        _layoutController.layoutCfg.linkDistance = function (d) {
          if (d.source.level !== 0) return 120;
          var length = 250;
          return length;
        };

        _layoutController.layoutCfg.edgeStrength = function () {
          return 2;
        };

        var tag = model.tag;
        var findTags = [];
        curShowNodesMap = new Map(); // find the nodes which are the descendants of clicked model

        nodes.forEach(function (node) {
          if (!node.tags) return;
          var tags = node.tags;
          var tlength = tags.length;
          var isChild = false;
          var parents = [];

          for (var i = 0; i < tlength; i++) {
            var ts = tags[i].split('-');

            if (ts[0] === tag) {
              isChild = true;
            }

            parents.push(nodeMap.get(ts[0]));
          }

          if (isChild) {
            var randomAngle = Math.random() * 2 * Math.PI;
            node.x = model.x + Math.cos(randomAngle) * model.size / 2 + 10;
            node.y = model.y + Math.sin(randomAngle) * model.size / 2 + 10; // const dist = (model.x - node.x) * (model.x - node.x) + (model.y - node.y) * (model.y - node.y);

            if (!node.style) node.style = {};
            node.style.lineWidth = 0;
            node.style.opacity = 1;

            if (node.isLeaf) {
              node.type = 'animate-circle';
              var color = 'l(0)';
              var parentsNum = parents.length;
              parents.forEach(function (parent, i) {
                var parentColor = parent.color.split(' ')[1].substr(2);
                color += " ".concat(i / (parentsNum - 1), ":").concat(parentColor);
              });

              if (parentsNum === 1) {
                color = model.color.split(' ')[1].substr(2);
              }

              node.color = color;
              node.style.fill = color;
              node.style.fill = '#fff';
              node.style.lineWidth = 1;
              node.size = 60;
              node.labelCfg = {
                style: {
                  fontSize: 11,
                  lineHeight: 19,
                  fill: '#697B8C'
                },
                position: 'center'
              };
            } else if (node.level !== 0) {
              node.type = 'circle'; // 'bubble';

              node.size = 95;
              if (!node.style) node.style = {};
              node.color = model.color;
              node.style.fill = model.color;
              node.labelCfg = {
                style: {
                  fill: '#fff',
                  fontSize: 14
                },
                position: 'center'
              };
            }

            curShowNodes.push(node);
            curShowNodesMap.set(node.id, node); // add the edge connect from model to node which exists in edges

            var edgeId = "".concat(model.id, "-").concat(node.id);
            var edge = edgesMap.get(edgeId);

            if (edge) {
              edge.color = model.color;
              curShowEdges.push(edge);
            }

            tags.forEach(function (t) {
              var ts = t.split('-');

              if (ts[0] !== tag) {
                findTags.push(ts[0]);
              }

              if (ts[1]) {
                findTags.push(ts[1]);
              }
            });
          }
        }); // find the nodes which are the ancestors of the current curShowNodes

        nodes.forEach(function (node) {
          var findTagsLength = findTags.length;

          for (var i = 0; i < findTagsLength; i++) {
            if (node.tag === findTags[i] && curShowNodesMap.get(node.id) === undefined) {
              curShowNodes.push(node);
              curShowNodesMap.set(node.id, node);
              return;
            }
          }
        }); // find the edges whose target end source are in the curShowNodes

        curShowNodes.forEach(function (nu, i) {
          var lu = nu.level;
          curShowNodes.forEach(function (nv, j) {
            if (j <= i) return;
            var lv = nv.level;
            var edgeId;

            if (lu < lv) {
              edgeId = "".concat(nu.id, "-").concat(nv.id);
            } else {
              edgeId = "".concat(nv.id, "-").concat(nu.id);
            }

            var color = model.color;

            if (nu.isLeaf) {
              if (nv.level === 0 && nv.tag !== model.tag) color = '#DFE5EB';else if (!nv.isLeaf && nv.tags[0] !== model.tag) {
                color = '#DFE5EB';
              }
            } else if (nv.isLeaf) {
              if (nu.level === 0 && nu.tag !== model.tag) color = '#DFE5EB';else if (!nu.isLeaf && nu.tags[0] !== model.tag) {
                color = '#DFE5EB';
              }
            }

            var edge = edgesMap.get(edgeId);

            if (edge) {
              edge.color = color;
              curShowEdges.push(edge);
            }
          });
        });
      }

      setTimeout(function () {
        graph.changeData({
          nodes: showNodes.concat(curShowNodes),
          edges: showEdges.concat(curShowEdges)
        });
        var nodeItems = graph.getNodes();
        var edgeItems = graph.getEdges();
        edgeItems.forEach(function (item) {
          graph.clearItemStates(item);
        });
        nodeItems.forEach(function (item) {
          graph.clearItemStates(item);
          graph.setItemState(item, 'appearing', true);
        });
      }, 400);
    }
  });
  graph.on('canvas:click', function () {
    currentFocus = undefined;
    var forceLayout = graph.get('layoutController').layoutMethods[0];
    forceLayout.forceSimulation.stop();
    var nodeItems = graph.getNodes();
    var edgeItems = graph.getEdges();

    if (highlighting) {
      highlighting = false;
      nodeItems.forEach(function (item) {
        graph.setItemState(item, 'dark', false);
      });
      edgeItems.forEach(function (item) {
        graph.setItemState(item, 'dark', false);
      });
    } else {
      nodeItems.forEach(function (item) {
        var model = item.getModel();

        if (model.level === 0) {
          graph.setItemState(item, 'dark', false);
        } else {
          graph.setItemState(item, 'disappearing', true);
        }
      });
      edgeItems.forEach(function (item) {
        graph.setItemState(item, 'disappearing', true);
      });
      curShowNodes = [];
      curShowEdges = [];
      setTimeout(function () {
        var layoutController = graph.get('layoutController');
        layoutController.layoutCfg.nodeStrength = 2500;
        layoutController.layoutCfg.collideStrength = 0.8;
        layoutController.layoutCfg.alphaDecay = 0.01;
        graph.changeData({
          nodes: showNodes,
          edges: showEdges
        });
      }, 400);
    }
  });
  if (typeof window !== 'undefined') window.onresize = function () {
    if (!graph || graph.get('destroyed')) return;
    if (!container || !container.scrollWidth || !container.scrollHeight) return;
    graph.changeSize(container.scrollWidth, container.scrollHeight);
  };
});

