    modes: {
      default: ['drag-canvas']
    },
    defaultNode: {
      type: 'bubble',
      size: 30,
      labelCfg: {
        position: 'center',
        style: {
          fill: 'white',
          fontStyle: 'bold'
        }
      }
    },
    defaultEdge: {
      color: '#888',
      type: 'animate-line'
    }
  });

  function translate(x, y) {
    var moveX = x;
    var moveY = y;
    /* 获得当前偏移量*/

    var group = graph.get('group');
    var bbox = group.getBBox();
    var leftTopPoint = graph.getCanvasByPoint(bbox.minX, bbox.minY);
    var rightBottomPoint = graph.getCanvasByPoint(bbox.maxX, bbox.maxY);
    /* 如果 x 轴在区域内，不允许左右超过100 */

    if (x < 0 && leftTopPoint.x - x > LIMIT_OVERFLOW_WIDTH) {
      moveX = 0;
    }

    if (x > 0 && rightBottomPoint.x - x < width - LIMIT_OVERFLOW_WIDTH) {
      moveX = 0;
    }

    if (y < 0 && leftTopPoint.y - y > LIMIT_OVERFLOW_HEIGHT) {
      moveY = 0;
    }

    if (y > 0 && rightBottomPoint.y - y < height - LIMIT_OVERFLOW_HEIGHT) {
      moveY = 0;
    }

