      var _loop = function _loop(i) {
        var delay = Math.random() * 1000;
        var start = shape.getPoint(i / circleCount);
        var circle = group.addShape('circle', {
          attrs: {
            x: start.x,
            y: start.y,
            r: 0.8,
            fill: '#A0F3AF',
            shadowColor: '#fff',
            shadowBlur: 30
          },
          name: 'circle-shape'
        });
        circle.animate(function (ratio) {
          ratio += i / circleCount;

          if (ratio > 1) {
            ratio %= 1;
          }

          var tmpPoint = shape.getPoint(ratio);
          return {
            x: tmpPoint.x,
            y: tmpPoint.y
          };
        }, {
          repeat: true,
          duration: 10 * length,
          easing: 'easeCubic',
          delay: delay
        });
      };

      for (var i = 0; i < circleCount; i++) {
        _loop(i);
      }
    }
  }, 'polyline');

  var graph = new _g.default.Graph({
    container: 'container',
    width: 500,
    height: 500,
    modes: {
      default: [{
        type: 'edge-tooltip',
        formatText: function formatText(model) {
          var text = model.class;
          return text;
        }
      }]
    },
    defaultNode: {
      type: 'breath-node',
      size: 3,
      style: {
        lineWidth: 0,
        fill: 'rgb(240, 223, 83)'
      }
    },
    defaultEdge: {
      type: 'running-polyline',
      size: 1,
      color: 'rgb(14,142,63)',
      style: {
        opacity: 0.4,
        lineAppendWidth: 3
      }
    }
  });
  var graphSize = [500, 500];
  fetch('https://gw.alipayobjects.com/os/basement_prod/8c2353b0-99a9-4a93-a5e1-3e7df1eac64f.json').then(function (res) {
    return res.json();
  }).then(function (data) {
    var nodes = data.nodes;
    var edges = data.edges;
    var classMap = new Map();
    var classId = 0;
    nodes.forEach(function (node) {
      node.y = -node.y;
    });
    edges.forEach(function (edge) {
      edge.id = "edge-".concat(edge.id); // edge cluster

      if (edge.class && classMap.get(edge.class) === undefined) {
        classMap.set(edge.class, classId);
        classId++;
      }

      var cid = classMap.get(edge.class);
      edge.color = colors[cid % colors.length];
      var controlPoints = edge.controlPoints;
      controlPoints.forEach(function (cp) {
        cp.y = -cp.y;
      });
    });
    scaleNodesPoints(nodes, edges, graphSize);
    graph.data(data);
    graph.render();
  });
  graph.get('container').style.background = '#000';
  graph.get('container').style.backgroundImage = 'url("https://gw.alipayobjects.com/mdn/rms_f8c6a0/afts/img/A*G23iRqkiibIAAAAAAAAAAABkARQnAQ")';
  graph.get('container').style.backgroundSize = '500px 500px';
  graph.get('container').style.backgroundRepeat = 'no-repeat';

  function scaleNodesPoints(nodes, edges, graphSize) {
    var size = graphSize[0] < graphSize[1] ? graphSize[0] : graphSize[1];
    var minX = 99999999999999999;
    var maxX = -99999999999999999;
    var minY = 99999999999999999;
    var maxY = -99999999999999999;
    nodes.forEach(function (node) {
      if (node.x > maxX) maxX = node.x;
      if (node.x < minX) minX = node.x;
      if (node.y > maxY) maxY = node.y;
      if (node.y < minY) minY = node.y;
    });
    edges.forEach(function (edge) {
      var controlPoints = edge.controlPoints;
      controlPoints.forEach(function (cp) {
        if (cp.x > maxX) maxX = cp.x;
        if (cp.x < minX) minX = cp.x;
        if (cp.y > maxY) maxY = cp.y;
        if (cp.y < minY) minY = cp.y;
      });
    });
    var xScale = maxX - minX;
    var yScale = maxY - minY;
    nodes.forEach(function (node) {
      node.orix = node.x;
      node.oriy = node.y;
      node.x = (node.x - minX) / xScale * size;
      node.y = (node.y - minY) / yScale * size;
    });
    edges.forEach(function (edge) {
      var controlPoints = edge.controlPoints;
      controlPoints.forEach(function (cp) {
        cp.x = (cp.x - minX) / xScale * size;
        cp.y = (cp.y - minY) / yScale * size;
      });
    });
  }
});

