    for (var i = 0; i < nodeLength; i++) {
      var node = nodes[i].getModel();
      positionMap[node.id] = {
        x: node.x,
        y: node.y,
        level: node.level
      };
    }

    return positionMap;
  };

  var stopLayout = function stopLayout() {
    layout.instance.stop();
  };

  var bindListener = function bindListener(graph) {
    graph.on('keydown', function (evt) {
      var code = evt.key;

      if (!code) {
        return;
      }

      if (code.toLowerCase() === 'shift') {
        shiftKeydown = true;
      } else {
        shiftKeydown = false;
      }
    });
    graph.on('keyup', function (evt) {
      var code = evt.key;

      if (!code) {
        return;
      }

      if (code.toLowerCase() === 'shift') {
        shiftKeydown = false;
      }
    });
    graph.on('node:mouseenter', function (evt) {
      var item = evt.item;
      var model = item.getModel();
      var currentLabel = model.label;
      model.oriFontSize = model.labelCfg.style.fontSize;
      item.update({
        label: model.oriLabel
      });
      model.oriLabel = currentLabel;
      graph.setItemState(item, 'hover', true);
      item.toFront();
    });
    graph.on('node:mouseleave', function (evt) {
      var item = evt.item;
      var model = item.getModel();
      var currentLabel = model.label;
      item.update({
        label: model.oriLabel
      });
      model.oriLabel = currentLabel;
      graph.setItemState(item, 'hover', false);
    });
    graph.on('edge:mouseenter', function (evt) {
      var item = evt.item;
      var model = item.getModel();
      var currentLabel = model.label;
      item.update({
        label: model.oriLabel
      });
      model.oriLabel = currentLabel;
      item.toFront();
      item.getSource().toFront();
      item.getTarget().toFront();
    });
    graph.on('edge:mouseleave', function (evt) {
      var item = evt.item;
      var model = item.getModel();
      var currentLabel = model.label;
      item.update({
        label: model.oriLabel
      });
      model.oriLabel = currentLabel;
    }); // click node to show the detail drawer

    graph.on('node:click', function (evt) {
      stopLayout();
      if (!shiftKeydown) clearFocusItemState(graph);else clearFocusEdgeState(graph);
      var item = evt.item; // highlight the clicked node, it is down by click-select

      graph.setItemState(item, 'focus', true);

      if (!shiftKeydown) {
        // 将相关边也高亮
        var relatedEdges = item.getEdges();
        relatedEdges.forEach(function (edge) {
          graph.setItemState(edge, 'focus', true);
        });
      }
    }); // click edge to show the detail of integrated edge drawer

    graph.on('edge:click', function (evt) {
      stopLayout();
      if (!shiftKeydown) clearFocusItemState(graph);
      var item = evt.item; // highlight the clicked edge

      graph.setItemState(item, 'focus', true);
    }); // click canvas to cancel all the focus state

    graph.on('canvas:click', function (evt) {
      clearFocusItemState(graph);
      console.log(graph.getGroup(), graph.getGroup().getBBox(), graph.getGroup().getCanvasBBox());
    });
  };

  fetch('https://gw.alipayobjects.com/os/antvdemo/assets/data/relations.json').then(function (res) {
    return res.json();
  }).then(function (data) {
    var container = document.getElementById('container');
    var descriptionDiv = document.createElement('div');
    descriptionDiv.innerHTML = "<a href='/en/largegraph' target='_blanck'>Click\u3010HERE\u3011To Full Demo</a>\n      <br/>\n      <a href='/zh/largegraph' target='_blanck'>\u70B9\u51FB\u3010\u8FD9\u91CC\u3011\u8FDB\u5165\u5B8C\u6574 Demo</a>";
    descriptionDiv.style.textAlign = 'right';
    descriptionDiv.style.color = '#fff';
    descriptionDiv.style.position = 'absolute';
    descriptionDiv.style.right = '32px';
    descriptionDiv.style.marginTop = '8px';
    container.appendChild(descriptionDiv);
    container.style.backgroundColor = '#2b2f33';
    CANVAS_WIDTH = container.scrollWidth;
    CANVAS_HEIGHT = (container.scrollHeight || 500) - 30;
    nodeMap = {};
    var clusteredData = louvain(data, false, 'weight');
    var aggregatedData = {
      nodes: [],
      edges: []
    };
    clusteredData.clusters.forEach(function (cluster, i) {
      cluster.nodes.forEach(function (node) {
        node.level = 0;
        node.label = node.id;
        node.type = '';
        node.colorSet = colorSets[i];
        nodeMap[node.id] = node;
      });
      var cnode = {
        id: cluster.id,
        type: 'aggregated-node',
        count: cluster.nodes.length,
        level: 1,
        label: cluster.id,
        colorSet: colorSets[i],
        idx: i
      };
      aggregatedNodeMap[cluster.id] = cnode;
      aggregatedData.nodes.push(cnode);
    });
    clusteredData.clusterEdges.forEach(function (clusterEdge) {
      var cedge = { ...clusterEdge,
        size: Math.log(clusterEdge.count),
        label: '',
        id: "edge-".concat(uniqueId())
      };

      if (cedge.source === cedge.target) {
        cedge.type = 'loop';
        cedge.loopCfg = {
          dist: 20
        };
      } else cedge.type = 'line';

      aggregatedData.edges.push(cedge);
    });
    data.edges.forEach(function (edge) {
      edge.label = "".concat(edge.source, "-").concat(edge.target);
      edge.id = "edge-".concat(uniqueId());
    });
    currentUnproccessedData = aggregatedData;

    var _processNodesEdges = processNodesEdges(currentUnproccessedData.nodes, currentUnproccessedData.edges, CANVAS_WIDTH, CANVAS_HEIGHT, largeGraphMode, true, true),
        processedEdges = _processNodesEdges.edges;

    var contextMenu = new _g.default.Menu({
      shouldBegin: function shouldBegin(evt) {
        if (evt.target && evt.target.isCanvas && evt.target.isCanvas()) return true;
        if (evt.item) return true;
        return false;
      },
      getContent: function getContent(evt) {
        var item = evt.item;

        if (evt.target && evt.target.isCanvas && evt.target.isCanvas()) {
          return "<ul>\n          <li id='show'>Show all Hidden Items</li>\n          <li id='collapseAll'>Collapse all Clusters</li>\n        </ul>";
        } else if (!item) return;

        var itemType = item.getType();
        var model = item.getModel();

        if (itemType && model) {
          if (itemType === 'node') {
            if (model.level !== 0) {
              return "<ul>\n              <li id='expand'>Expand the Cluster</li>\n              <li id='hide'>Hide the Node</li>\n            </ul>";
            } else {
              return "<ul>\n              <li id='collapse'>Collapse the Cluster</li>\n              <li id='neighbor-1'>Find 1-degree Neighbors</li>\n              <li id='neighbor-2'>Find 2-degree Neighbors</li>\n              <li id='neighbor-3'>Find 3-degree Neighbors</li>\n              <li id='hide'>Hide the Node</li>\n            </ul>";
            }
          } else {
            return "<ul>\n            <li id='hide'>Hide the Edge</li>\n          </ul>";
          }
        }
      },
      handleMenuClick: function handleMenuClick(target, item) {
        var model = item && item.getModel();
        var liIdStrs = target.id.split('-');
        var mixedGraphData;

        switch (liIdStrs[0]) {
          case 'hide':
            graph.hideItem(item);
            hiddenItemIds.push(model.id);
            break;

          case 'expand':
            var newArray = manageExpandCollapseArray(graph.getNodes().length, model, collapseArray, expandArray);
            expandArray = newArray.expandArray;
            collapseArray = newArray.collapseArray;
            mixedGraphData = getMixedGraph(clusteredData, data, nodeMap, aggregatedNodeMap, expandArray, collapseArray);
            break;

          case 'collapse':
            var aggregatedNode = aggregatedNodeMap[model.clusterId];
            manipulatePosition = {
              x: aggregatedNode.x,
              y: aggregatedNode.y
            };
            collapseArray.push(aggregatedNode);

            for (var i = 0; i < expandArray.length; i++) {
              if (expandArray[i].id === model.clusterId) {
                expandArray.splice(i, 1);
                break;
              }
            }

            mixedGraphData = getMixedGraph(clusteredData, data, nodeMap, aggregatedNodeMap, expandArray, collapseArray);
            break;

          case 'collapseAll':
            expandArray = [];
            collapseArray = [];
            mixedGraphData = getMixedGraph(clusteredData, data, nodeMap, aggregatedNodeMap, expandArray, collapseArray);
            break;

          case 'neighbor':
            var expandNeighborSteps = parseInt(liIdStrs[1]);
            mixedGraphData = getNeighborMixedGraph(model, expandNeighborSteps, data, clusteredData, currentUnproccessedData, nodeMap, aggregatedNodeMap, 10);
            break;

          case 'show':
            showItems(graph);
            break;

          default:
            break;
        }

        if (mixedGraphData) {
          cachePositions = cacheNodePositions(graph.getNodes());
          currentUnproccessedData = mixedGraphData;
          handleRefreshGraph(graph, currentUnproccessedData, CANVAS_WIDTH, CANVAS_HEIGHT, largeGraphMode, true, false);
        }
      },
      // offsetX and offsetY include the padding of the parent container
      // 需要加上父级容器的 padding-left 16 与自身偏移量 10
      offsetX: 16 + 10,
      // 需要加上父级容器的 padding-top 24 、画布兄弟元素高度、与自身偏移量 10
      offsetY: 0,
      // the types of items that allow the menu show up
      // 在哪些类型的元素上响应
      itemTypes: ['node', 'edge', 'canvas']
    });
    graph = new _g.default.Graph({
      container: 'container',
      width: CANVAS_WIDTH,
      height: CANVAS_HEIGHT,
      linkCenter: true,
      minZoom: 0.1,
      groupByTypes: false,
      modes: {
        default: [{
          type: 'drag-canvas',
          enableOptimize: true
        }, {
          type: 'zoom-canvas',
          enableOptimize: true,
          optimizeZoom: 0.01
        }, 'drag-node', 'shortcuts-call'],
        lassoSelect: [{
          type: 'zoom-canvas',
          enableOptimize: true,
          optimizeZoom: 0.01
        }, {
          type: 'lasso-select',
          selectedState: 'focus',
          trigger: 'drag'
        }],
        fisheyeMode: []
      },
      defaultNode: {
        type: 'aggregated-node',
        size: DEFAULTNODESIZE
      },
      plugins: [contextMenu]
    });
    graph.get('canvas').set('localRefresh', false);
    var layoutConfig = getForceLayoutConfig(graph, largeGraphMode);
    layoutConfig.center = [CANVAS_WIDTH / 2, CANVAS_HEIGHT / 2];
    layout.instance = new _g.default.Layout['gForce'](layoutConfig);
    layout.instance.init({
      nodes: currentUnproccessedData.nodes,
      edges: processedEdges
    });
    layout.instance.execute();
    bindListener(graph);
    graph.data({
      nodes: aggregatedData.nodes,
      edges: processedEdges
    });
    graph.render();
  });
  if (typeof window !== 'undefined') window.onresize = function () {
    if (!graph || graph.get('destroyed')) return;
    var container = document.getElementById('container');
    if (!container) return;
    graph.changeSize(container.scrollWidth, container.scrollHeight - 30);
  };
});

