          name: 'node-path-shape'
        });
      }

      var mainX = x - 10;
      var mainY = -height + 15;

      if (rootNode) {
        group.addShape('rect', {
          attrs: {
            x: mainX,
            y: mainY,
            width: width + 12,
            height: height,
            radius: 14,
            fill: '#e8f7ff',
            cursor: 'pointer'
          },
          name: 'main-shape'
        });
      }

      var nameColor = 'rgba(0, 0, 0, .65)';

      if (selected) {
        nameColor = '#40A8FF';
      } // 名称


      if (rootNode) {
        group.addShape('text', {
          attrs: {
            text: label,
            x: mainX + rootPadding,
            y: 1,
            textAlign: 'left',
            textBaseline: 'middle',
            fill: nameColor,
            fontSize: 12,
            fontFamily: 'PingFangSC-Regular',
            cursor: 'pointer'
          },
          name: 'root-text-shape'
        });
      } else {
        group.addShape('text', {
          attrs: {
            text: label,
            x: selected ? mainX + 6 + nameMarginLeft : mainX + 6,
            y: y - 5,
            textAlign: 'start',
            textBaseline: 'top',
            fill: nameColor,
            fontSize: 12,
            fontFamily: 'PingFangSC-Regular',
            cursor: 'pointer'
          },
          name: 'not-root-text-shape'
        });
      } // 子类数量


      if (hasChildren && !rootNode) {
        var childCountHeight = 12;
        var childCountX = width - childCountWidth;
        var childCountY = -childCountHeight / 2;
        group.addShape('rect', {
          attrs: {
            width: childCountWidth,
            height: 12,
            stroke: collapsed ? '#1890ff' : '#5CDBD3',
            fill: collapsed ? '#fff' : '#E6FFFB',
            x: childCountX,
            y: childCountY,
            radius: 6,
            cursor: 'pointer'
          },
          name: 'child-count-rect-shape'
        });
        group.addShape('text', {
          attrs: {
            text: "".concat(children === null || children === void 0 ? void 0 : children.length),
            fill: 'rgba(0, 0, 0, .65)',
            x: childCountX + childCountWidth / 2,
            y: childCountY + 12,
            fontSize: 10,
            width: childCountWidth,
            textAlign: 'center',
            cursor: 'pointer'
          },
          name: 'child-count-text-shape'
        });
      }

      return keyShape;
    }
  });

  _g.default.registerEdge('smooth', {
    draw: function draw(cfg, group) {
      var startPoint = cfg.startPoint,
          endPoint = cfg.endPoint;
      var hgap = Math.abs(endPoint.x - startPoint.x);
      var path = [['M', startPoint.x, startPoint.y], ['C', startPoint.x + hgap / 4, startPoint.y, endPoint.x - hgap / 2, endPoint.y, endPoint.x, endPoint.y]];
      var shape = group.addShape('path', {
