const container = document.getElementById('container');
const width = container.scrollWidth;
const height = (container.scrollHeight || 500) - 20;

const graph = new G6.Graph({
  container: 'container',
  width,
  height,
  fitView: true,
  modes: {
    default: ['drag-canvas', 'drag-node', 'zoom-canvas'],
  },
});

const legendDataMap = {}
data.nodes.forEach((node, i) => {
  const colorSet = colorSets[(+node.cluster.split('-')[1])];
  node.style = {
    fill: colorSet.mainFill,
    stroke: colorSet.mainStroke,
  }
  if (!legendDataMap[node.cluster]) {
    legendDataMap[node.cluster] = {
      shape: 'circle',
      r: 6,
      text: node.cluster,
      fill: colorSet.mainFill,
      stroke: colorSet.mainStroke,
    }
  }
})
data.edges.forEach((edge, i) => {
  const colorSet = colorSets[(+edge.cluster.split('-')[1])];
  edge.style = {
    stroke: colorSet.mainStroke,
    lineWidth: 2,
    opacity: 0.3,
    endArrow: true
  }

  if (!legendDataMap[edge.cluster]) {
    legendDataMap[edge.cluster] = {
      shape: 'line',
      text: edge.cluster,
      stroke: colorSet.mainStroke,
      lineWidth: 2,
      opacity: 0.3,
      endArrow: true
    }
  }
})
graph.data(data);
graph.render();

// pattern graph data
