  // });

  _g.default.registerNode('bubble', {
    drawShape: function drawShape(cfg, group) {
      var self = this;
      var r = cfg.size / 2; // a circle by path

      var path = [['M', -r, 0], ['C', -r, r / 2, -r / 2, r, 0, r], ['C', r / 2, r, r, r / 2, r, 0], ['C', r, -r / 2, r / 2, -r, 0, -r], ['C', -r / 2, -r, -r, -r / 2, -r, 0], ['Z']];
      var keyShape = group.addShape('path', {
        attrs: {
          x: 0,
          y: 0,
          path: path,
          fill: cfg.color || 'steelblue'
        },
        // must be assigned in G6 3.3 and later versions. it can be any value you want
        name: 'path-shape'
      });
      var mask = group.addShape('path', {
        attrs: {
          x: 0,
          y: 0,
          path: path,
          opacity: 0.25,
          fill: cfg.color || 'steelblue',
          shadowColor: cfg.color,
          // cfg.color.split(' ')[2].substr(2),
          shadowBlur: 40,
          shadowOffsetX: 0,
          shadowOffsetY: 30
        },
        // must be assigned in G6 3.3 and later versions. it can be any value you want
        name: 'mask-shape'
      });
      var spNum = 10; // split points number

      var directions = [],
          rs = [];
      self.changeDirections(spNum, directions);

      for (var i = 0; i < spNum; i++) {
        var rr = r + directions[i] * (Math.random() * r / 1000); // +-r/6, the sign according to the directions

        if (rs[i] < 0.97 * r) rs[i] = 0.97 * r;else if (rs[i] > 1.03 * r) rs[i] = 1.03 * r;
        rs.push(rr);
      }

      keyShape.animate(function () {
        var path = self.getBubblePath(r, spNum, directions, rs);
        return {
          path: path
        };
      }, {
        repeat: true,
        duration: 1000
      });
      var directions2 = [],
          rs2 = [];
      self.changeDirections(spNum, directions2);

      for (var _i = 0; _i < spNum; _i++) {
        var _rr = r + directions2[_i] * (Math.random() * r / 1000); // +-r/6, the sign according to the directions


        if (rs2[_i] < 0.97 * r) rs2[_i] = 0.97 * r;else if (rs2[_i] > 1.03 * r) rs2[_i] = 1.03 * r;
        rs2.push(_rr);
      }

      mask.animate(function () {
        var path = self.getBubblePath(r, spNum, directions2, rs2);
        return {
          path: path
        };
      }, {
        repeat: true,
        duration: 1000
      });
      return keyShape;
    },
    changeDirections: function changeDirections(num, directions) {
      for (var i = 0; i < num; i++) {
        if (!directions[i]) {
          var rand = Math.random();
          var dire = rand > 0.5 ? 1 : -1;
          directions.push(dire);
        } else {
          directions[i] = -1 * directions[i];
        }
      }

      return directions;
    },
    getBubblePath: function getBubblePath(r, spNum, directions, rs) {
      var path = [];
      var cpNum = spNum * 2; // control points number

      var unitAngle = Math.PI * 2 / spNum; // base angle for split points

      var angleSum = 0;
      var sps = [];
      var cps = [];

      for (var i = 0; i < spNum; i++) {
        var speed = 0.001 * Math.random();
        rs[i] = rs[i] + directions[i] * speed * r; // +-r/6, the sign according to the directions

        if (rs[i] < 0.97 * r) {
          rs[i] = 0.97 * r;
          directions[i] = -1 * directions[i];
        } else if (rs[i] > 1.03 * r) {
          rs[i] = 1.03 * r;
          directions[i] = -1 * directions[i];
        }

        var spX = rs[i] * Math.cos(angleSum);
        var spY = rs[i] * Math.sin(angleSum);
        sps.push({
          x: spX,
          y: spY
        });

        for (var j = 0; j < 2; j++) {
          var cpAngleRand = unitAngle / 3;
          var cpR = rs[i] / Math.cos(cpAngleRand);
          var sign = j === 0 ? -1 : 1;
          var x = cpR * Math.cos(angleSum + sign * cpAngleRand);
          var y = cpR * Math.sin(angleSum + sign * cpAngleRand);
          cps.push({
            x: x,
            y: y
          });
        }

        angleSum += unitAngle;
      }

      path.push(['M', sps[0].x, sps[0].y]);

      for (var _i2 = 1; _i2 < spNum; _i2++) {
        path.push(['C', cps[2 * _i2 - 1].x, cps[2 * _i2 - 1].y, cps[2 * _i2].x, cps[2 * _i2].y, sps[_i2].x, sps[_i2].y]);
      }

      path.push(['C', cps[cpNum - 1].x, cps[cpNum - 1].y, cps[0].x, cps[0].y, sps[0].x, sps[0].y]);
      path.push(['Z']);
      return path;
    }
  }, 'single-node');

  _g.default.registerNode('animate-circle', {
    setState: function setState(name, value, item) {
      var shape = item.get('keyShape');
      var label = shape.get('parent').get('children')[1];

      if (name === 'disappearing' && value) {
        shape.animate(function (ratio) {
          return {
            opacity: 1 - ratio,
            r: shape.attr('r') * (1 - ratio)
          };
        }, {
          duration: 200
        });
        label.animate(function (ratio) {
          return {
            opacity: 1 - ratio
          };
        }, {
          duration: 500
        });
      } else if (name === 'appearing' && value) {
        var r = item.getModel().size / 2;
        shape.animate(function (ratio) {
          return {
            opacity: ratio,
            r: r * ratio,
            fill: shape.attr('fill')
          };
        }, {
          duration: 300
        });
        label.animate(function (ratio) {
          return {
            opacity: ratio
          };
        }, {
          duration: 500
        });
      }
    }
  }, 'circle');

  _g.default.registerEdge('animate-line', {
    drawShape: function drawShape(cfg, group) {
      var self = this;
      var shapeStyle = self.getShapeStyle(cfg);
      shapeStyle = mix(shapeStyle, {
        opacity: 0,
        strokeOpacity: 0
      });
      var keyShape = group.addShape('path', {
        attrs: shapeStyle,
        // must be assigned in G6 3.3 and later versions. it can be any value you want
        name: 'path-shape'
      });
      return keyShape;
    },
    afterDraw: function afterDraw(cfg, group) {
      var shape = group.get('children')[0];
      shape.animate(function (ratio) {
        var opacity = ratio * cfg.style.opacity;
        var strokeOpacity = ratio * cfg.style.strokeOpacity;
        return {
          opacity: ratio || opacity,
          strokeOpacity: ratio || strokeOpacity
        };
      }, {
        duration: 300
      });
    },
    setState: function setState(name, value, item) {
      var shape = item.get('keyShape');

      if (name === 'disappearing' && value) {
        shape.animate(function (ratio) {
          return {
            opacity: 1 - ratio,
            strokeOpacity: 1 - ratio
          };
        }, {
          duration: 200
        });
      }
    }
  }, 'line');

  graph = new _g.default.Graph({
    container: 'container',
    width: width,
    height: height,
    linkCenter: true,
    layout: layoutCfg,
    modes: {
      default: ['drag-canvas']
    },
    defaultNode: {
      type: 'bubble',
      size: 30,
      labelCfg: {
        position: 'center',
        style: {
          fill: 'white',
          fontStyle: 'bold'
        }
      }
    },
    defaultEdge: {
      color: '#888',
      type: 'animate-line'
    }
  });

  function translate(x, y) {
    var moveX = x;
    var moveY = y;
    /* 获得当前偏移量*/

    var group = graph.get('group');
    var bbox = group.getBBox();
    var leftTopPoint = graph.getCanvasByPoint(bbox.minX, bbox.minY);
    var rightBottomPoint = graph.getCanvasByPoint(bbox.maxX, bbox.maxY);
    /* 如果 x 轴在区域内，不允许左右超过100 */

    if (x < 0 && leftTopPoint.x - x > LIMIT_OVERFLOW_WIDTH) {
      moveX = 0;
    }

    if (x > 0 && rightBottomPoint.x - x < width - LIMIT_OVERFLOW_WIDTH) {
      moveX = 0;
    }

    if (y < 0 && leftTopPoint.y - y > LIMIT_OVERFLOW_HEIGHT) {
      moveY = 0;
    }

    if (y > 0 && rightBottomPoint.y - y < height - LIMIT_OVERFLOW_HEIGHT) {
      moveY = 0;
    }

    graph.translate(-moveX, -moveY);
  }

  function refreshDragedNodePosition(e) {
    var model = e.item.get('model');
    model.fx = e.x;
    model.fy = e.y;
  }

  graph.on('node:dragstart', function (e) {
    graph.layout();
    refreshDragedNodePosition(e);
  });
  graph.on('node:drag', function (e) {
    refreshDragedNodePosition(e);
  });
  graph.on('node:dragend', function (e) {
    e.item.get('model').fx = null;
    e.item.get('model').fy = null;
  });

  var loadData = function loadData(data) {
    var layoutController = graph.get('layoutController');
    layoutController.layoutCfg.nodeStrength = 30;
    layoutController.layoutCfg.collideStrength = 0.8;
    layoutController.layoutCfg.alphaDecay = 0.01;
    nodes = data.nodes;
    edges = data.edges;
    showNodes = [];
    showEdges = [];
    nodeMap = new Map();
    edgesMap = new Map(); // find the roots

    nodes.forEach(function (node) {
      if (!node.neighbor) {
        node.label = node.text;
        node.color = Colors[node.type];
        node.style = {
          fill: Colors[node.type],
          lineWidth: 0
        };
        var labelColor = '#fff';

        if (node.type === 'adj') {
          labelColor = Colors.noun;
        }

        node.labelCfg = {
          style: {
            fontSize: 15,
            fill: labelColor,
            fontWeight: 300
          }
        };
        node.x = Math.random() * 800;
        node.y = Math.random() * 800;
        showNodes.push(node);
      }

      nodeMap.set(node.id, node);
    });
    mapNodeSizeAndFontSize(showNodes, 'count', [40, 120]);
    showNodes.forEach(function (snode) {
      if (snode.size < 80) {
        snode.type = 'circle';
      }
    });
    edges.forEach(function (edge) {
      // map the id
      edge.id = "".concat(edge.source, "-").concat(edge.target);
      edge.style = {
        lineWidth: 0.5,
        opacity: 1,
        strokeOpacity: 1
      };
      edgesMap.set(edge.id, edge);
    });
    graph.data({
      nodes: showNodes,
      edges: showEdges
    });
    graph.render();
  };

  fetch('https://gw.alipayobjects.com/os/basement_prod/0a749386-8593-44a2-a132-81dfa1fc3158.json').then(function (res) {
    return res.json();
  }).then(function (data) {
    loadData(data);
  }); // click root to expand

  graph.on('node:click', function (e) {
    curShowNodes = [];
    curShowEdges = [];
    var item = e.item;
    var model = item.getModel();

    if (model.neighbor) {
      return;
    } // if clicked a root, hide unrelated items and show the related items


    if (!model.neighbor) {
      var layoutController = graph.get('layoutController');
      var forceLayout = layoutController.layoutMethods[0];
      forceLayout.forceSimulation.stop(); // light the level 0 nodes

      showNodes.forEach(function (snode) {
        if (snode.x < 0.5 * width) {
          snode.x = 300;
        } else {
          snode.x = width - 300;
        }
      });
      model.x = width / 2;
      model.y = height / 2; // animatively hide the items which are going to disappear

      if (curShowEdges.length) {
        curShowEdges.forEach(function (csedge) {
          var item = graph.findById(csedge.id);
          item && graph.setItemState(item, 'disappearing', true);
        });
      }

      curShowNodes.forEach(function (csnode) {
        var item = graph.findById(csnode.id);
        item && graph.setItemState(item, 'disappearing', true);
      });
      graph.positionsAnimate(); // reset curShowNodes nad curShowEdges

      curShowNodes = [];
      curShowEdges = []; // click on the same node which is the current focus node, hide the small nodes, change the layout parameters to roots view

      if (currentFocus && currentFocus.id === model.id) {
        currentFocus = undefined;
        layoutController.layoutCfg.nodeStrength = 100;
        layoutController.layoutCfg.collideStrength = 0.8;
        layoutController.layoutCfg.alphaDecay = 0.01;
      } else {
        // click other focus node, hide the current small nodes and show the related nodes
        currentFocus = model; // change data after the original items disappearing

        var _layoutController = graph.get('layoutController');

        _layoutController.layoutCfg.nodeStrength = function () {
          return -80;
        };

        _layoutController.layoutCfg.collideStrength = 0.2;

        _layoutController.layoutCfg.linkDistance = function (d) {
          if (!d.source.neighbor && !d.target.neighbor) return 150;
          return 80;
        };

        _layoutController.layoutCfg.edgeStrength = function () {
          return 2;
        };

        curShowNodesMap = new Map(); // find the nodes which are the descendants of clicked model

        edges.forEach(function (edge) {
          var nodeId = '';
          var node;

          if (edge.source === model.id) {
            nodeId = edge.target;
          } else if (edge.target === model.id) {
            nodeId = edge.source;
          } else {
            return;
          }

          nodes.forEach(function (tnode) {
            if (tnode.id === nodeId) {
              node = tnode;
            }
          });
          if (!node) return;
          var randomAngle = Math.random() * 2 * Math.PI;
          node.x = model.x + Math.cos(randomAngle) * model.size / 2 + 10;
          node.y = model.y + Math.sin(randomAngle) * model.size / 2 + 10; // const dist = (model.x - node.x) * (model.x - node.x) + (model.y - node.y) * (model.y - node.y);

          if (!node.style) node.style = {};
          node.style.lineWidth = 0;
          node.style.opacity = 1;

          if (node.neighbor) {
            node.type = 'animate-circle';
            node.label = node.text;
            var color = model.style.fill;
            node.color = color;
            node.style.fill = '#fff';
            node.style.lineWidth = 1;
            node.size = 30;
            node.labelCfg = {
              style: {
                fontSize: 13,
                lineHeight: 19,
                fill: '#697B8C'
              },
              position: 'center'
            };
          }

          curShowNodes.push(node);
          curShowNodesMap.set(node.id, node); // add the edge connect from model to node which exists in edges

          var edgeId = "".concat(model.id, "-").concat(node.id);
          var medge = edgesMap.get(edgeId);

          if (medge) {
            medge.color = model.color;
            curShowEdges.push(medge);
          }
        });
        edges.forEach(function (edge) {
          if (edge.source === model.id || edge.target === model.id) {
            curShowEdges.push(edge);
          }
        });
      }

      setTimeout(function () {
        graph.changeData({
          nodes: showNodes.concat(curShowNodes),
          edges: showEdges.concat(curShowEdges)
        });
        var nodeItems = graph.getNodes();
        var edgeItems = graph.getEdges();
        edgeItems.forEach(function (item) {
          graph.clearItemStates(item);
        });
        nodeItems.forEach(function (item) {
          graph.clearItemStates(item);
          graph.setItemState(item, 'appearing', true);
        });
      }, 400);
    }
  });
  graph.on('canvas:click', function () {
    currentFocus = undefined;
    var forceLayout = graph.get('layoutController').layoutMethods[0];
    forceLayout.forceSimulation.stop();
    var nodeItems = graph.getNodes();
    var edgeItems = graph.getEdges();

    if (highlighting) {
      highlighting = false;
    } else {
      nodeItems.forEach(function (item) {
        var model = item.getModel();

        if (model.neighbor) {
          graph.setItemState(item, 'disappearing', true);
        }
      });
      edgeItems.forEach(function (item) {
        graph.setItemState(item, 'disappearing', true);
      });
      curShowNodes = [];
      curShowEdges = [];
      setTimeout(function () {
        var layoutController = graph.get('layoutController');
        layoutController.layoutCfg.nodeStrength = 100;
        layoutController.layoutCfg.collideStrength = 0.8;
        layoutController.layoutCfg.alphaDecay = 0.01;
        graph.changeData({
          nodes: showNodes,
          edges: showEdges
        });
      }, 400);
    }
  });
  if (typeof window !== 'undefined') window.onresize = function () {
    if (!graph || graph.get('destroyed')) return;
    if (!container || !container.scrollWidth || !container.scrollHeight) return;
    graph.changeSize(container.scrollWidth, container.scrollHeight);
  };
});

